from django.contrib.auth.backends import ModelBackendimport refrom .models import Userdef jwt_response_payload_handler(token, user=None, request=None):    """重写JWT登录视图的构造响应数据函数,多追加 user_id和username"""    return {        'token': token,        'user_id': user.id,        'username': user.username    }def get_user_by_account(account):    """    通过传入的账号动态获取user 模型对象    :param account:  有可以是手机号,有可能是用户名    :return:  user或None    """    user = None    if re.match(r'1[3-9]\d{9}$', account):        user = User.objects.filter(mobile=account).first()    if not user:  # 手机号中没找到，则再通过用户名查找        user = User.objects.filter(username=account).first()    return userclass UsernameMobileAuthBackend(ModelBackend):    """修改Django的认证类,为了实现多账号登录"""    def authenticate(self, request, username=None, password=None, **kwargs):        # 获取到user        user = get_user_by_account(username)        # 判断当前前端传入的密码是否正确        if user and user.check_password(password):            # 返回user            return user